using UnityEngine;using System.Collections;using MS.Model;using System;namespace MS.Views.Editor.UI{    public class OwnableMapElementView : MS.View<MS.Model.OwnableMapElement>    {        public MS.UI.ComboBoxManager ComboBox;        public override void UpdateView()        {            UpdateView(m_Model);        }        public override void UpdateView(OwnableMapElement element)        {            ComboBox.Clear();                        foreach (MS.Model.Player player in Managers.GameManager.Instance.Game.Players)            {                ComboBox.AddItem(player.Name);            }            ComboBox.SelectItem(element.Owner.Name);        }    }}